<script lang="ts" setup>
import { useState } from "#app";
import { computed, ref, watch } from "vue";

// تعریف وضعیت
const source = useState("source", () => "");

// متن پیش‌فرض
const defaultHeading = "ارتباط ما";

// محاسبه متن heading بر اساس وضعیت
const headingText = computed(() => {
  return source.value || defaultHeading;
});

// فرم دیتا
const formData = ref({
  name: '',
  phone: '',
  message: '',
  package: computed(() => source.value || '-') // اضافه کردن فیلد package
});

const error = ref('');
const success = ref('');
const isSubmitting = ref(false);

// متغیرهای مربوط به نوتیفیکیشن
const showToast = ref(false);
const toastMessage = ref('');

// خطاهای اعتبارسنجی
const validationErrors = ref({
  name: '',
  phone: '',
  message: ''
});

// بررسی معتبر بودن فرم
const isFormValid = computed(() => {
  return !validationErrors.value.name && 
         !validationErrors.value.phone && 
         !validationErrors.value.message &&
         formData.value.name.trim() !== '' &&
         formData.value.phone.trim() !== '' &&
         formData.value.message.trim() !== '';
});

// نمایش نوتیفیکیشن
const showNotification = (message: string) => {
  toastMessage.value = message;
  showToast.value = true;
  
  // بستن نوتیفیکیشن بعد از 3 ثانیه
  setTimeout(() => {
    showToast.value = false;
  }, 5000);
};

// اعتبارسنجی یک فیلد خاص
const validateField = (field: string) => {
  if (field === 'name') {
    if (!formData.value.name.trim()) {
      validationErrors.value.name = 'لطفا نام خود را وارد کنید';
    } else {
      validationErrors.value.name = '';
    }
  } else if (field === 'phone') {
    if (!formData.value.phone.trim()) {
      validationErrors.value.phone = 'لطفا شماره تماس خود را وارد کنید';
    } else if (!/^(09|\+989)\d{9}$/.test(formData.value.phone)) {
      validationErrors.value.phone = 'لطفا یک شماره تلفن معتبر وارد کنید';
    } else {
      validationErrors.value.phone = '';
    }
  } else if (field === 'message') {
    if (!formData.value.message.trim()) {
      validationErrors.value.message = 'لطفا پیام خود را وارد کنید';
    } else {
      validationErrors.value.message = '';
    }
  }
};

// اعتبارسنجی کل فرم
const validateForm = () => {
  validateField('name');
  validateField('phone');
  validateField('message');
  
  return isFormValid.value;
};

// تابع ارسال فرم
const handleSubmit = async () => {
  try {
    // اعتبارسنجی فرم قبل از ارسال
    if (!validateForm()) {
      error.value = 'لطفا خطاهای فرم را برطرف کنید';
      return;
    }
    
    isSubmitting.value = true;
    error.value = '';
    success.value = '';

    const response = await fetch('/api/contact', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name: formData.value.name,
        phone: formData.value.phone,
        message: formData.value.message,
        package: source.value || '-' // استفاده مستقیم از source
      })
    });

    const data = await response.json();

    if (!response.ok) {
      throw new Error(data.message || 'خطا در ارسال پیام');
    }

    if (data.success) {
      success.value = 'پیام شما با موفقیت ارسال شد';
      // نمایش نوتیفیکیشن
      showNotification('پیام شما با موفقیت ارسال شد');
      
      formData.value = {
        name: '',
        phone: '',
        message: '',
        package: formData.value.package // حفظ مقدار package
      };
    } else {
      throw new Error(data.message || 'خطا در ارسال پیام');
    }
  } catch (err) {
    error.value = err instanceof Error ? err.message : 'خطا در ارسال پیام';
  } finally {
    isSubmitting.value = false;
  }
};

// پاک کردن پیام‌های موفقیت و خطا هنگام تغییر در فرم
watch(() => [formData.value.name, formData.value.phone, formData.value.message], () => {
  error.value = '';
  success.value = '';
});
</script>

